router.get('/', async(req, res, next) => {


    Post.find()
        .select('title photoUrl description comment')
        .exec()
        .then(docs => {
            const response = {
                count: docs.length,
                blogsData: docs.map(doc => {
                    return {
                        title: doc.title,
                        photoUrl: doc.photoUrl,
                        description: doc.description,
                        _id: doc._id,
                        date: doc.date,
                        comment: doc.comment,
                        likes: doc.likes,
